package primeiroProjeto;

public class lista18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		macaxeira();
		mandionca();
		mandioca();
		quadradinho();
		pair();
		man();
		trianglo();
	}
	public static void macaxeira() {
		for(int i = 0; i <7; i++) {
			System.out.println("emily");
		}
	}
	
	public static void mandionca() {
		System.out.println("embrance the pain, the frailty, and the moments so unbearably shameful. forgive yourself...because everything begins from there.");
	}
	
	public static void mandioca() {
		for(int i=1; i <= 10;i++) {
			System.out.println(i);
		}
	}
	
	public static void quadradinho() {
		System.out.println("*****");
		System.out.println("*   *");
		System.out.println("*****");
	}
	
	public static void pair() {
		for(int i = 2; i <=20;i++) {
			if(i%2 == 0) {
				System.out.println(i);
			}
		}
	}
	
	public static void man() {
		for(int i =10; i>=1; i--) {
			System.out.println(i);
		}System.out.println("fim :3");
	}
	
	public static void trianglo(){
		System.out.println("*");
		System.out.println("**");
		System.out.println("***");
		System.out.println("****");
		System.out.println("*****");
	}
	
}
